function decodeFrmPayload(input) {
    var output = {
        attributes: {}, telemetry: {}
    };

    // --- Decoding code --- //
    var decoded = {};
    for (var i = 0; i < input.length;) {
        var type = input[i++] & 0xff;

        if (type == 0x01) { 
            // Temperature Converts to °C
            decoded.temparature = parseBytesToInt(input, i, 2, true) / 10;
            i += 2;
        } else if (type == 0x02) { // Humidity
            decoded.humidity = input[i];
            i += 1;
        } else if (type == 0x04) { // Light
            decoded.light = parseBytesToInt(input, i, 2, true);
            i += 2;
        } else if (type == 0x05) { // Pir
            decoded.pir = input[i];
            i += 1;
        } else if (type == 0x06) { // CO₂
            decoded.co2 = parseBytesToInt(input, i, 2, true);
            i += 2;
        } else if (type == 0x07) { // Battery Voltage (VDD)
            decoded.vdd = parseBytesToInt(input, i, 2, true);   
            i += 2;
        }
    }

    output.telemetry = [{
        ts: timestamp,
        values: decoded
    }];

    // --- Decoding code --- //
    return output;
}


