// If you want to parse incoming data somehow, you can add your code to this function.
// input: bytes
// expected output:
//  {
//    "attributes": {"attributeKey": "attributeValue"},
//    "telemetry": [{"ts": 1...1, "values": {"telemetryKey":"telemetryValue"}, {"ts": 1...2, "values":                      {"telemetryKey":"telemetryValue"}}]
//  }

function decodePayload(input) {
    var output = {
        attributes: {},
        telemetry: []
    };

    // --- Decoding code --- //
    var decoded = {};
    var fPort = data.fPort;
    var historyDataList = [];
    if(fPort == 85) {
        for (var i = 0; i < input.length - 2;) {
            var channel_id = input[i++] & 0xff;
            var channel_type = input[i++] & 0xff;

            // IPSO VERSION
            if (channel_id === 0xff && channel_type === 0x01) {
                output.attributes.ipso_version = readProtocolVersion(bytes[i]);
                i += 1;
            }
            // HARDWARE VERSION
            else if (channel_id === 0xff && channel_type === 0x09) {
                output.attributes.hardware_version = readHardwareVersion(bytes.slice(i, i + 2));
                i += 2;
            }
            // FIRMWARE VERSION
            else if (channel_id === 0xff && channel_type === 0x0a) {
                output.attributes.firmware_version = readFirmwareVersion(bytes.slice(i, i + 2));
                i += 2;
            }
            // DEVICE STATUS
            else if (channel_id === 0xff && channel_type === 0x0b) {
                output.attributes.device_status = "on";
                i += 1;
            }
            // LORAWAN CLASS TYPE
            else if (channel_id === 0xff && channel_type === 0x0f) {
                output.attributes.lorawan_class = readLoRaWANType(bytes[i]);
                i += 1;
            }
            // SERIAL NUMBER
            else if (channel_id === 0xff && channel_type === 0x16) {
                output.attributes.sn = readSerialNumber(bytes.slice(i, i + 8));
                i += 8;
            }
            // TSL VERSION
            else if (channel_id === 0xff && channel_type === 0xff) {
                output.attributes.tsl_version = readTslVersion(bytes.slice(i, i + 2));
                i += 2;
            }
            // BATTERY
            if (channel_id === 0x01 && channel_type === 0x75) {
                decoded.battery = input[i];
                i += 1;
            }
            // TEMPERATURE
            else if (channel_id === 0x03 && channel_type === 0x67) {
                // ℃
                decoded.temperature = parseBytesToInt(input, i, 2, false) / 10;
                i += 2;

                // ℉
                // decoded.temperature = parseBytesToInt(input, i, 2, false) / 10 * 1.8 + 32;
                // i +=2;
            }
            // HUMIDITY
            else if (channel_id === 0x04 && channel_type === 0x68) {
                decoded.humidity = parseBytesToInt(input, i, 1, false) / 2;
                i += 1;
            }
            // TEMPERATURE & HUMIDITY HISTROY
            else if (channel_id === 0x20 && channel_type === 0xce) {
                var historyData = {
                    ts: parseBytesToInt(input, i, 4, false) * 1000,
                    values: {
                        temperature: parseBytesToInt(input, i, 2, false) / 10,
                        humidity: input[i + 6] /2
                    }
                };

                historyDataList.add(historyData);
                i += 8;
            }
        }
    }

    output.telemetry = [{
        ts: timestamp,
        values: decoded
    }];

    output.telemetry.addAll(historyDataList);

    // --- Decoding code --- //
    return output;
}

// --- attributes and telemetry objects ---
var telemetry = [];
var attributes = {};
// --- attributes and telemetry objects ---

// --- Timestamp parsing
var dateString = data.time;
timestamp = parseDateToTimestamp(dateString);
// --- Timestamp parsing

// Passing incoming bytes to decodePayload function, to get custom decoding
var customDecoding = decodePayload(base64ToBytes(data.data));

// Collecting data to result
if (customDecoding.?telemetry.size() > 0) {
    foreach(telemetryObj: customDecoding.telemetry) {
        if (telemetryObj.ts != null && telemetryObj.values != null) {
            telemetry.add(telemetryObj);
        }
    }
}

if (customDecoding.?attributes.size() > 0) {
    attributes.putAll(customDecoding.attributes);
}

// You can add some keys manually to attributes or telemetry
attributes.eui = data.?devEui;
attributes.fPort = data.fPort;
attributes.applicationId = data.?applicationId;
attributes.applicationName = data.?applicationName;
attributes.frequency = data.txInfo.?frequency;
attributes.bandwidth = data.txInfo.?dataRate.?bandwidth;
attributes.spreadingFactor = data.txInfo.?dataRate.?spreadFactor;
attributes.codeRate = data.txInfo.?codeRate;

if(Boolean.parseBoolean(metadata["includeGatewayInfo"])) {
   var gatewayInfo = getGatewayInfo();
   var addDataToTelemetry = {};
   addDataToTelemetry.snr = gatewayInfo.?loRaSNR;
   addDataToTelemetry.rssi = gatewayInfo.rssi;
   addDataToTelemetry.fCnt = data.fCnt;

   telemetry = processTelemetryData(telemetry, addDataToTelemetry);
}

var result = {
    deviceName: deviceName,
    deviceType: deviceType,
    //  assetName: assetName,
    //  assetType: assetType,
    attributes: attributes,
    telemetry: telemetry
};

addAdditionalInfoForDeviceMsg(result, customerName, groupName);

return result;

function addAdditionalInfoForDeviceMsg(deviceInfo, customerName, groupName) {
    if (customerName != null) {
        deviceInfo.customerName = customerName;
    }
    if (groupName != null) {
        deviceInfo.groupName = groupName;
    }
}

function parseDateToTimestamp(dateString) {
    dateString = dateString.replaceFirst("(\\d{4}-\\d{2})(\\d{2})", "$1-$2");
    dateString = dateString.replaceFirst("\\.(\\d{3})\\d+Z", ".$1Z");
    dateString = dateString.replace("Z", "+0000");
    var date = new Date(dateString);
    var timestamp = date.getTime();

    // If we cannot parse timestamp - we will use the current timestamp
    if (timestamp == -1) {
       timestamp = Date.now();
    }

    return timestamp;
}

function getGatewayInfo() {
    var gatewayList = data.rxInfo;
    var maxRssi = Integer.MIN_VALUE;
    var gatewayInfo = {};

    foreach (gateway : gatewayList) {
        if(gateway.rssi > maxRssi) {
            maxRssi = gateway.rssi;
            gatewayInfo = gateway;
        }
    }

    return gatewayInfo;
}

function processTelemetryData(telemetry, addDataToTelemetry) {
    if (telemetry.size >= 1) {
        telemetry = addDataToTelemetries(telemetry, addDataToTelemetry);
    }
    else {
        telemetry.add(addDataToTelemetry);
    }

    return telemetry;
}

function addDataToTelemetries(telemetries, addDataToTelemetry) {
    foreach(telemetry : telemetries) {
        foreach(element : addDataToTelemetry.entrySet()) {
            if(!telemetry["values"].keys.contains(element.key)) {
                telemetry["values"][element.key] = element.value;
            }
        } 
    }

    return telemetries;
}

function readProtocolVersion(bytes) {
    var major = (bytes & 0xf0) >> 4;
    var minor = bytes & 0x0f;
    return "v" + major + "." + minor;
}

function readHardwareVersion(bytes) {
    var major = bytes[0] & 0xff;
    var minor = (bytes[1] & 0xff) >> 4;
    return "v" + major + "." + minor;
}

function readFirmwareVersion(bytes) {
    var major = bytes[0] & 0xff;
    var minor = bytes[1] & 0xff;
    return "v" + major + "." + minor;
}

function readTslVersion(bytes) {
    var major = bytes[0] & 0xff;
    var minor = bytes[1] & 0xff;
    return "v" + major + "." + minor;
}

function readSerialNumber(bytes) {
    var temp = [];
    for (var idx = 0; idx < bytes.length; idx++) {
        temp.push(("0" + (bytes[idx] & 0xff).toString(16)).slice(-2));
    }
    return temp.join("");
}

function readLoRaWANType(type) {
    switch (type) {
        case 0x00:
            return "ClassA";
        case 0x01:
            return "ClassB";
        case 0x02:
            return "ClassC";
        case 0x03:
            return "ClassCtoB";
        default:
            return "Unknown";
    }
}
