var data = decodeToJson(payload);
var deviceName = "VS121 " +  data.EUI;
var deviceType = "VS121";
var groupName = null; // If groupName is not null - created device will be added to the entity group with such name.
var customerName = null; // If customerName is not null - created devices will be assigned to customer with such name. 

// use assetName and assetType instead of deviceName and deviceType
// to automatically create assets instead of devices.
// var assetName = 'Asset A';
// var assetType = 'building';

// If you want to parse incoming data somehow, you can add your code to this function.
// input: bytes
// expected output:
//  {
//    "attributes": {"attributeKey": "attributeValue"},
//    "telemetry": {"telemetryKey": "telemetryValue"}
//  }

function decodePayload(input) {
    var output = { attributes: {}, telemetry: []};

    var decoded = {};
    var fPort = data.port;
    if(fPort == 85) {
        for(var i = 0; i < input.length - 2; ) {
            var channel_id = input[i++] & 0xff;
            var channel_type = input[i++] & 0xff;

            // PROTOCOL VESION
            if (channel_id === 0xff && channel_type === 0x01) {
                output.attributes.protocol_version = input[i];
                i += 1;
            }
            // SERIAL NUMBER
            else if (channel_id === 0xff && channel_type === 0x08) {
                output.attributes.sn = readSerialNumber(input.slice(i, i + 6));
                i += 6;
            }
            // HARDWARE VERSION
            else if (channel_id === 0xff && channel_type === 0x09) {
                output.attributes.hardware_version = readVersion(input.slice(i, i + 2));
                i += 2;
            }
            // FIRMWARE VERSION
            else if (channel_id === 0xff && channel_type === 0x1f) {
                output.attributes.firmware_version = readVersion(input.slice(i, i + 4));
                i += 4;
            }
            // PEOPLE COUNTER
            else if (channel_id === 0x04 && channel_type === 0xc9) {
                decoded.people_count_all = input[i];
                decoded.region_count = input[i + 1];
                var region = parseBytesToInt(input, i + 2, 2);
                for (var idx = 0; idx < decoded.region_count; idx++) {
                    var tmp = "region_" + (idx + 1);
                    decoded.tmp = (region >> idx) & 1;
                }
                i += 4;
            }
            // PEOPLE IN/OUT
            else if (channel_id === 0x05 && channel_type === 0xcc) {
                decoded.people_in = parseBytesToInt(input, i, 2);
                decoded.people_out = parseBytesToInt(input, i + 2, 2);
                i += 4;
            }
            // PEOPLE MAX
            else if (channel_id === 0x06 && channel_type === 0xcd) {
                decoded.people_count_max = input[i];
                i += 1;
            }
             // REGION COUNTER
            else if (channel_id === 0x07 && channel_type === 0xd5) {
                decoded.region_1_count = input[i];
                decoded.region_2_count = input[i + 1];
                decoded.region_3_count = input[i + 2];
                decoded.region_4_count = input[i + 3];
                decoded.region_5_count = input[i + 4];
                decoded.region_6_count = input[i + 5];
                decoded.region_7_count = input[i + 6];
                decoded.region_8_count = input[i + 7];
                i += 8;
            }
            // REGION COUNTER
            else if (channel_id === 0x08 && channel_type === 0xd5) {
                decoded.region_9_count =  input[i];
                decoded.region_10_count = input[i + 1];
                decoded.region_11_count = input[i + 2];
                decoded.region_12_count = input[i + 3];
                decoded.region_13_count = input[i + 4];
                decoded.region_14_count = input[i + 5];
                decoded.region_15_count = input[i + 6];
                decoded.region_16_count = input[i + 7];
                i += 8;
            }
            // A FLOW
            else if (channel_id === 0x09 && channel_type === 0xda) {
                decoded.a_to_a = parseBytesToInt(input, i, 2);
                decoded.a_to_b = parseBytesToInt(input, i + 2, 2);
                decoded.a_to_c = parseBytesToInt(input, i + 4, 2);
                decoded.a_to_d = parseBytesToInt(input, i + 6, 2);
                i += 8;
            }
            // B FLOW
            else if (channel_id === 0x0a && channel_type === 0xda) {
                decoded.b_to_a = parseBytesToInt(input, i, 2);
                decoded.b_to_b = parseBytesToInt(input, i + 2, 2);
                decoded.b_to_c = parseBytesToInt(input, i + 4, 2);
                decoded.b_to_d = parseBytesToInt(input, i + 6, 2);
                i += 8;
            }
            // C FLOW
            else if (channel_id === 0x0b && channel_type === 0xda) {
                decoded.c_to_a = parseBytesToInt(input, i, 2);
                decoded.c_to_b = parseBytesToInt(input, i + 2, 2);
                decoded.c_to_c = parseBytesToInt(input, i + 4, 2);
                decoded.c_to_d = parseBytesToInt(input, i + 6, 2);
                i += 8;
            }
            // D FLOW
            else if (channel_id === 0x0c && channel_type === 0xda) {
                decoded.d_to_a = parseBytesToInt(input, i, 2);
                decoded.d_to_b = parseBytesToInt(input, i + 2, 2);
                decoded.d_to_c = parseBytesToInt(input, i + 4, 2);
                decoded.d_to_d = parseBytesToInt(input, i + 6, 2);
                i += 8;
            }
            // TOTAL IN/OUT
            else if (channel_id === 0x0d && channel_type === 0xcc) {
                decoded.people_total_in = parseBytesToInt(input, i, 2);
                decoded.people_total_out = parseBytesToInt(input, i + 2, 2);
                i += 4;
            }
            // DWELL TIME
            else if (channel_id === 0x0e && channel_type === 0xe4) {
                var region = input[i];
                // decoded.region = region;
                decoded.dwell_time_avg = parseBytesToInt(input, i + 1, 2);
                decoded.dwell_time_max = parseBytesToInt(input, i + 3, 2);
                i += 5;
            }
            // TIMESTAMP
            //else if (channel_id === 0x0f && channel_type === 0x85) {
               // decoded.timestamp = readUInt32LE(bytes.slice(i, i + 4));
                //i += 4;
            //}
        }
    }

    output.telemetry = [{
        ts: timestamp,
        values: decoded
    }];

    // --- Decoding code --- //
    return output;
}

// --- attributes and telemetry objects ---
var telemetry = [];
var attributes = {};
// --- attributes and telemetry objects ---

// --- Timestamp parsing
timestamp = data.ts;
// --- Timestamp parsing

// Message parsing
// To avoid paths in the decoded objects we passing false value to function as "pathInKey" argument.
// Warning: pathInKey can cause already found fields to be overwritten with the last value found.

var uplinkDataList = [];

// Passing incoming bytes to decodePayload function, to get custom decoding
var customDecoding = decodePayload(hexToBytes(data.data));

// Collecting data to result
if (customDecoding.?telemetry.size() > 0) {
    if (customDecoding.telemetry instanceof java.util.ArrayList) {
        foreach(telemetryObj: customDecoding.telemetry) {
            if (telemetryObj.ts != null && telemetryObj.values != null) {
                telemetry.add(telemetryObj);
            }
        }
    } else {
        telemetry.putAll(customDecoding.telemetry);
    }
}

if (customDecoding.?attributes.size() > 0) {
    attributes.putAll(customDecoding.attributes);
}

// You can add some keys manually to attributes or telemetry
attributes.eui = data.EUI;
attributes.fPort = data.port;
attributes.frequency = data.freq;

var isIncludeGatewayInfo = metadata["includeGatewayInfo"];
if(isIncludeGatewayInfo == true) {
    var addDataToTelemetry = {};
    addDataToTelemetry.rssi = data.rssi;
    addDataToTelemetry.seqno = data.seqno;
    addDataToTelemetry.snr = data.snr;
    addDataToTelemetry.ack = data.ack;
    addDataToTelemetry.toa = data.toa;
    addDataToTelemetry.fCnt = data.fcnt;

   telemetry = processTelemetryData(telemetry, addDataToTelemetry);
}

var deviceInfo = {
    deviceName: deviceName,
    deviceType: deviceType,
//  assetName: assetName,
//  assetType: assetType,
    attributes: attributes,
    telemetry: telemetry, 
};

addAdditionalInfoForDeviceMsg(deviceInfo, customerName, groupName);

uplinkDataList.add(deviceInfo);

var gatewayDeviceNamePrefix = "Gateway ";
var gatewayDeviceType = "Lora gateway";
var gatewayGroupName = null; // If gatewayGroupName is not null - created device will be added to the entity group with such name.

if (data.cmd == "gw") {
    foreach( gatewayInfo : data.gws ) {
        var addGatewayInfo = {};

        // You can add some keys manually telemetry
        addGatewayInfo.rssi = gatewayInfo.rssi;
        addGatewayInfo.snr = gatewayInfo.snr;
        // You can add some keys manually telemetry

        var gatewayInfoMsg = {
            deviceName: gatewayDeviceNamePrefix + gatewayInfo.gweui,
            deviceType: gatewayDeviceType,
            telemetry: [{
                "ts": gatewayInfo.ts,
                "values": addGatewayInfo
            }],
            attributes: {
                eui: gatewayInfo.gweui
            }
        };
        addAdditionalInfoForDeviceMsg(gatewayInfoMsg, customerName, gatewayGroupName);
        uplinkDataList.add(gatewayInfoMsg);
    }
}

return uplinkDataList;

function addAdditionalInfoForDeviceMsg(deviceInfo, customerName, groupName) {
    if (customerName != null) {
        deviceInfo.customerName = customerName;
    }
    if (groupName != null) {
        deviceInfo.groupName = groupName;
    }
}

function processTelemetryData(telemetry, addDataToTelemetry) {
    if (telemetry.size >= 1) {
        telemetry = addDataToTelemetries(telemetry, addDataToTelemetry);
    }
    else {
        telemetry.add(addDataToTelemetry);
    }

    return telemetry;
}

function addDataToTelemetries(telemetries, addDataToTelemetry) {
    foreach(telemetry : telemetries) {
        foreach(element : addDataToTelemetry.entrySet()) {
            if(!telemetry["values"].keys.contains(element.key)) {
                telemetry["values"][element.key] = element.value;
            }
        } 
    }

    return telemetries;
}

function readVersion(byteList) {
    var temp = "";
    for (int idx = 0; idx < byteList.length; idx++) {
        temp += (byteList[idx] & 0xFF);
        if (idx < byteList.length - 1) {
            temp += ".";
        }
    }

    return temp;
}

function readSerialNumber(byteList) {
    var serialNumberResult = "";
    for (int idx = 0; idx < byteList.length; idx++) {
        serialNumberResult += String.format("%02x", byteList[idx] & 0xFF);
    }

    return serialNumberResult;
}
