function decodeFrmPayload(input) {
    var output = {
        attributes: {}, telemetry: {}
    };
    // --- Decoding code --- //
    var fPort = data.uplink_message.f_port;
    var decoded = {};
    if(fPort == 6) {
        if(input[1] == 0x02 && input[2] == 0x01) {
            decoded.battery_voltage = parseBytesToInt(input, 3, 1) / 10;
            decoded.contact_switch = input[4] == 0x01 ? "on" : "off";
        }
        else if(input[1] == 0x02 && input[2] == 0x00) {
            output.attributes.softwareVersion = input[3] / 10;
            output.attributes.hardwareVersion = input[4];
            output.attributes.dateCode = decodeDateCodeToTimestamp(java.util.Arrays.copyOfRange(input, 5, 9));
        }
    }
    else if(fPort == 7) {
        if(input[1] == 0x02 && (input[0] & 0xff) == 0x81) {
            decoded.cmd = "configReportRsp";
            decoded.status = input[2] == 0x00 ? "success" : "failure";
        }
        else if(input[1] == 0x02 && (input[0] & 0xff) == 0x82) {
            decoded.cmd = "readConfigReportRsp";
            decoded.minTime = parseBytesToInt(input, 2, 2);
            decoded.maxTime = parseBytesToInt(input, 4, 2);
            decoded.batteryChange = parseBytesToInt(input, 6, 1) / 10;
        }
        else if(input[1] == 0x02 && (input[0] & 0xff) == 0x83) {
            decoded.cmd = "setOnOffStateDurationRsp";
            decoded.status = input[2] == 0x00 ? "success" : "failure";
        }
        else if(input[1] == 0x02 && (input[0] & 0xff) == 0x84) {
            decoded.cmd = "getOnOffStateDurationRsp";
            decoded.on_state_duration = parseBytesToInt(input, 2, 2);
            decoded.off_state_duration = parseBytesToInt(input, 4, 2);
        }
        else if((input[1] & 0xff) == 0x1F && (input[0] & 0xff) == 0xFF) {
            decoded.cmd = "setLastMessageResendtimeRsp";
            decoded.status = input[2] == 0x00 ? "success" : "failure";
        }
        else if((input[1] & 0xff)== 0x1E && (input[0] & 0xff) == 0xFE) {
            decoded.cmd = "getLastMessageResendtimeRsp";
            decoded.resend_time = input[2] & 0xff;
        }
    }

    output.telemetry = [{
        ts: timestamp,
        values: decoded
    }];

    // --- Decoding code --- //
    return output;
}


