function decodeFrmPayload(input) {
    var output = { attributes: {}, telemetry: []};

    // --- Decoding code --- //
    var fPort = data.uplink_message.f_port;
    var decoded = {};
    if(fPort == 6) {
        if(input[1] == 0x13 && input[2] == 0x01) {
            decoded.battery_voltage = parseBytesToInt(input, 3, 1) / 10;
            decoded.temperature = parseBytesToInt(input, 4, 2) / 100;
            decoded.humidity = parseBytesToInt(input, 6, 2) / 100;
        }
        if(input[1] == 0x13 && input[2] == 0x00) {
            output.attributes.softwareVersion = input[3] / 10;
            output.attributes.hardwareVersion = input[4];
            output.attributes.dateCode = decodeDateCodeToTimestamp(java.util.Arrays.copyOfRange(input, 5, 9));
        }
    }
    if(fPort == 7) {
        if(input[1] == 0x13 && (input[0] & 0xff) == 0x81) {
            decoded.cmd = "configReportRsp";
            decoded.status = input[2] == 0x00 ? "success" : "failure";
        }
        if(input[1] == 0x13 && (input[0] & 0xff) == 0x82) {
            decoded.cmd = "readConfigReportRsp";
            decoded.minTime = parseBytesToInt(input, 2, 2);
            decoded.maxTime = parseBytesToInt(input, 4, 2);
            decoded.batteryChange = parseBytesToInt(input, 6, 1) / 10;
            decoded.tempChange = parseBytesToInt(input, 7, 2) / 100;
            decoded.humiChange = parseBytesToInt(input, 9, 2) / 100;
        }
    }
    if(fPort == 14) {
        if((input[0] & 0xff) == 0x81) {
            decoded.cmd = "setGlobalCalibrateRsp";
            decoded.sensor_type = input[1] == 0x01 ? "temperature sensor" : "humidity sensor"; 
            decoded.channel = input[2] == 0x00 ? "temperature" : "humidity";
            decoded.status = input[3] == 0x00 ? "success" : "failure";
        }
        if((input[0] & 0xff) == 0x82) {
            decoded.cmd = "getGlobalCalibrateRsp";
            decoded.sensor_type = setSensorType(input[1]);
            decoded.channel = setChannel(input[2]);
            decoded.multiplier = parseBytesToInt(input, 3, 2) & 0xff;
            decoded.divisor = parseBytesToInt(input, 5, 2) & 0xff;
            decoded.deltValue = parseBytesToInt(input, 7, 2);
        }
        if((input[0] & 0xff) == 0x83) {
            decoded.cmd = "clearGlobalCalibrateRsp";
            decoded.status = input[1] == 0x00 ? "success" : "failure";
        }
    }
    if(fPort == 0x10) {
        if((input[0] & 0xff) == 0x81) {
            decoded.cmd = "setSensorAlarmThresholdRsp";
            decoded.status = input[1] == 0x00 ? "success" : "failure";
        }
        if((input[0] & 0xff) == 0x82) {
            decoded.cmd = "getSensorAlarmThresholdRsp";
            decoded.channel = setChannel(input[1]);
            decoded.sensor_type = setSensorType(input[2]);
            decoded.sensor_high_threshold = parseBytesToInt(input, 3, 4) / 100;
            decoded.sensor_low_threshold = parseBytesToInt(input, 7, 4) / 100;
        }
    }
    if(fPort == 0x20) {
        if((input[0] & 0xff) == 0x81) {
            decoded.cmd = "setNetvoxLoRaWANRejoinRsp";
            decoded.status = input[1] == 0x00 ? "success" : "failure";
        }
        if((input[0] & 0xff) == 0x82) {
            decoded.cmd = "getNetvoxLoRaWANRejoinRsp";
            decoded.rejoin_check_period = parseBytesToInt(input, 1, 4);
            decoded.rejoin_threshold = input[5];
        }
    }


    output.telemetry = [{
        ts: timestamp,
        values: decoded
    }];

    // --- Decoding code --- //
    return output;
}

