function decodePayload(input) {
    var output = {
        attributes: {},
        telemetry: []
    };

    // --- Decoding code --- //
    var fPort = data.fPort;
    var decoded = {};
    if(fPort == 6) {
        if(input[1] == 0x09 && input[2] == 0x07) {
            decoded.battery_volt = parseBytesToInt(input, 3, 1) / 10;
            decoded.noise = parseBytesToInt(input, 8, 2) / 10;
        }
        else if(input[1] == 0x09 && input[2] == 0x0C) {
            decoded.battery_volt = parseBytesToInt(input, 3, 1) / 10;
            decoded.temperature = parseBytesToInt(input, 4, 2) / 100;
            decoded.humidity = parseBytesToInt(input, 6, 2) / 100;
        }
    }
    else if(fPort == 7) {
        if(input[1] == 0x09 && (input[0] & 0xff) == 0x81) {
            decoded.cmd = "configReportRsp";
            decoded.status = input[2] == 0x00 ? "success" : "failure";
        }
        else if(input[1] == 0x09 && (input[0] & 0xff) == 0x82) {
            decoded.cmd = "readConfigReportRsp";
            decoded.minTime = parseBytesToInt(input, 2, 2);
            decoded.maxTime = parseBytesToInt(input, 4, 2);
        }
    }
    else if(fPort == 0x0E) {
        if((input[0] & 0xff) == 0x81) {
            decoded.cmd = "setGlobalCalibrateRsp";
            decoded.sensor_type = getSensorType(input[1]);
            decoded.channel = input[2] + 0x01;
            decoded.status = input[3] == 0x00 ? "success" : "failure";
        }
        else if((input[0] & 0xff) == 0x82) {
            decoded.cmd = "getGlobalCalibrateRsp";
            decoded.sensor_type = getSensorType(input[1]);
            decoded.channel = input[2] + 0x01;
            decoded.multiplier = parseBytesToInt(input, 2, 2);
            decoded.divisor = parseBytesToInt(input, 4, 2);
            decoded.deltValue = parseBytesToInt(input, 6, 2);
        }
    }

    output.telemetry = [{
        ts: timestamp,
        values: decoded
    }];

    // --- Decoding code --- //
    return output;
}

