function decodeFrmPayload(input) {
    var output = { attributes: {}, telemetry: []};

    var fPort = data.uplink_message.f_port;
    // --- Decoding code --- //
    var decoded = {};
    if (fPort === 1) { // Counter update
        decoded.type = "counter_update";
        decoded.counter_value = (input[0] << 8) | input[1];
    } else if (fPort === 2) { // Heartbeat
        decoded.type = "heartbeat";
        decoded.hw_health_status = input[0] & 0x7F;
        var batteryVoltageMv = 2500 + input[1] * 4;
        decoded.battery_voltage = batteryVoltageMv / 1000;

        var batteryMeanVoltageMv = 2500 + input[2] * 4;
        decoded.battery_voltage_mean_24h = batteryMeanVoltageMv / 1000;

    } else if (fPort === 3) { // Startup
        decoded.type = "startup";
        decoded.firmware_version = input[0] + "." + input[1] + "." + input[2];
        decoded.reset_cause = [
            "rejoining_lorawan_network",
            "watchdog",
            "power_on",
            "user_request",
            undefined,
            undefined,
            "brownout",
            "other"
        ];

        var resetCauseIndex = input[3];
        if (resetCauseIndex >= 0 && resetCauseIndex < resetCauses.length) {
            decoded.reset_cause = resetCauses[Integer.parseInt(resetCauseIndex)];
        } else {
            decoded.reset_cause = "unknown";
        }

    } else if (fPort === 6) { // Debug
        decoded.type = "debug";
        decoded.bytes = input.bytes;
    }

    output.telemetry = [{
        ts: timestamp,
        values: decoded
    }];

    // --- Decoding code --- //
    return output;
}

// --- attributes and telemetry objects ---
var telemetry = [];
var attributes = {};
// --- attributes and telemetry objects ---

// --- Timestamp parsing
var dateString = data.uplink_message.received_at;

if ((data.simulated != null && data.simulated) || dateString == null) {
    dateString = data.received_at;
}

timestamp = parseDateToTimestamp(dateString);
// --- Timestamp parsing

// Message parsing
// To avoid paths in the decoded objects we passing false value to function as "pathInKey" argument.
// Warning: pathInKey can cause already found fields to be overwritten with the last value found, e.g. receive_at from uplink_message will be written receive_at in the root.

// Passing incoming bytes to decodeFrmPayload function, to get custom decoding
var customDecoding = {};
if (data.uplink_message.get("frm_payload") != null) {
    customDecoding = decodeFrmPayload(base64ToBytes(data.uplink_message.frm_payload));
}

// Collecting data to result
if (customDecoding.?telemetry.size() > 0) {
    if (customDecoding.telemetry instanceof java.util.ArrayList) {
