var data = decodeToJson(payload);
var deviceName = "G4 Parking sensor FM " + data.EUI;
var deviceType = "Parking sensor";
var groupName = null; // If groupName is not null - created device will be added to the entity group with such name.
var customerName = null; // If customerName is not null - created devices will be assigned to customer with such name. 

// use assetName and assetType instead of deviceName and deviceType
// to automatically create assets instead of devices.
// var assetName = 'Asset A';
// var assetType = 'building';

// If you want to parse incoming data somehow, you can add your code to this function.
// input: bytes
// expected output:
//  {
//    "attributes": {"attributeKey": "attributeValue"},
//    "telemetry": {"telemetryKey": "telemetryValue"}
//  }

function decodePayload(input) {
    var output = { attributes: {}, telemetry: []};

    // --- Decoding code --- //
    var decoded = {};
    var fPort = data.port;

    if (fPort === 1) { // Parking status
        decoded.type = "parking_status";
        decoded.occupied = (input[0] & 0x1) === 0x1;
        var previousState = calculatePreviousState((input[0] >> 1) & 0x7F);
        decoded.previous_state_duration = previousState.previous_state_duration;
        decoded.previous_state_duration_error = previousState.previous_state_duration_error;
        decoded.previous_state_duration_overflow = previousState.previous_state_duration_overflow;
    } 
    else if (fPort === 2) { // Heartbeat
        decoded.type = "heartbeat";
        decoded.occupied = (input[0] & 0x1) === 0x1;
        decoded.hw_health_status = input[0] & 0x7F;

        var temp_byte = (input[2] > 127) ? input[2] - 256 : input[2];
        decoded.temperature = temp_byte / 2 + 10;

        var batteryVoltageMv = 2500 + input[1] * 4;
        decoded.battery_voltage = batteryVoltageMv / 1000;

        var criticalThreshold;
        var lowThreshold;

        if (decoded.temperature >= -5) {
            criticalThreshold = 2900;
            lowThreshold = 3000;
        } else if (decoded.temperature < -5) {
            criticalThreshold = 2800;
            lowThreshold = 2900;
        }

        if (batteryVoltageMv >= lowThreshold) {
            decoded.battery_health = "normal";
        } 
        else if (batteryVoltageMv >= criticalThreshold && batteryVoltageMv < lowThreshold) {
            decoded.battery_health = "low";
        } 
        else if (batteryVoltageMv < criticalThreshold) {
            decoded.battery_health = "critical";
        }
    } 
    else if (fPort === 3) { // Startup
        decoded.type = "startup";
        decoded.firmware_version = input[0] + "." + input[1] + "." + input[2];
        var resetCauses = [
            "rejoining_lorawan_network", // 0x00
            "watchdog",                  // 0x01
            "power_on",                  // 0x02
            "user_request",              // 0x03
            "unknown",                   // 0x04 (not defined)
            "unknown",                   // 0x05 (not defined)
            "brownout",                  // 0x06
            "other"                      // 0x07
        ];

        var resetCauseIndex = input[3];
        if (resetCauseIndex >= 0 && resetCauseIndex < resetCauses.length) {
            decoded.reset_cause = resetCauses.get(Integer.parseInt(resetCauseIndex));
        } 
        else {
            decoded.reset_cause = "unknown";
        }
        decoded.occupied = (input[4] & 0x1) === 0x1;
    } 
    else if (fPort === 6) { // Debug
        decoded.type = "debug";
        decoded.bytes = input;
    } 
    else if (fPort === 10) { // SDI tag registration
        decoded.type = "user_registration";

        if ((input[0] & 0x1) === 0x1) {
            decoded.occupied = true;
            previousState = calculatePreviousState((input[0] >> 1) & 0x7F);
            decoded.previous_state_duration = previousState.previous_state_duration;
            decoded.previous_state_duration_error = previousState.previous_state_duration_error;
            decoded.previous_state_duration_overflow = previousState.previous_state_duration_overflow;
        } else {
            decoded.occupied = null;
            decoded.previous_state_duration = null;
            decoded.previous_state_duration_error = null;
            decoded.previous_state_duration_overflow = null;
        }

        var tagId = (input[1] << 24) | (input[2] << 16) | (input[3] << 8) | input[4];
        decoded.tag_id = Integer.toHexString(tagId).toUpperCase();
    }

    output.telemetry = [{
        ts: timestamp,
        values: decoded
    }];

    // --- Decoding code --- //
    return output;
}

// --- attributes and telemetry objects ---
var telemetry = [];
var attributes = {};
// --- attributes and telemetry objects ---

// --- Timestamp parsing
timestamp = data.ts;
// --- Timestamp parsing

// Message parsing
// To avoid paths in the decoded objects we passing false value to function as "pathInKey" argument.
// Warning: pathInKey can cause already found fields to be overwritten with the last value found.

var uplinkDataList = [];

// Passing incoming bytes to decodePayload function, to get custom decoding
var customDecoding = decodePayload(hexToBytes(data.data));

// Collecting data to result
if (customDecoding.?telemetry.size() > 0) {
    if (customDecoding.telemetry instanceof java.util.ArrayList) {
        foreach(telemetryObj: customDecoding.telemetry) {
            if (telemetryObj.ts != null && telemetryObj.values != null) {
                telemetry.add(telemetryObj);
            }
        }
    } else {
        telemetry.putAll(customDecoding.telemetry);
    }
}

if (customDecoding.?attributes.size() > 0) {
    attributes.putAll(customDecoding.attributes);
}

// You can add some keys manually to attributes or telemetry
attributes.eui = data.EUI;
attributes.fPort = data.port;
attributes.frequency = data.freq;

var isIncludeGatewayInfo = metadata["includeGatewayInfo"];
if(isIncludeGatewayInfo == true) {
    var addDataToTelemetry = {};
    addDataToTelemetry.rssi = data.rssi;
    addDataToTelemetry.seqno = data.seqno;
    addDataToTelemetry.snr = data.snr;
    addDataToTelemetry.ack = data.ack;
    addDataToTelemetry.toa = data.toa;
    addDataToTelemetry.fCnt = data.fcnt;

   telemetry = processTelemetryData(telemetry, addDataToTelemetry);
}

var deviceInfo = {
    deviceName: deviceName,
    deviceType: deviceType,
//  assetName: assetName,
//  assetType: assetType,
    attributes: attributes,
    telemetry: telemetry, 
};

addAdditionalInfoForDeviceMsg(deviceInfo, customerName, groupName);

uplinkDataList.add(deviceInfo);

var gatewayDeviceNamePrefix = "Gateway ";
var gatewayDeviceType = "Lora gateway";
var gatewayGroupName = null; // If gatewayGroupName is not null - created device will be added to the entity group with such name.

if (data.cmd == "gw") {
    foreach( gatewayInfo : data.gws ) {
        var addGatewayInfo = {};

        // You can add some keys manually telemetry
        addGatewayInfo.rssi = gatewayInfo.rssi;
        addGatewayInfo.snr = gatewayInfo.snr;
        // You can add some keys manually telemetry

        var gatewayInfoMsg = {
            deviceName: gatewayDeviceNamePrefix + gatewayInfo.gweui,
            deviceType: gatewayDeviceType,
            telemetry: [{
                "ts": gatewayInfo.ts,
                "values": addGatewayInfo
            }],
            attributes: {
                eui: gatewayInfo.gweui
            }
        };
        addAdditionalInfoForDeviceMsg(gatewayInfoMsg, customerName, gatewayGroupName);
        uplinkDataList.add(gatewayInfoMsg);
    }
}

return uplinkDataList;

function addAdditionalInfoForDeviceMsg(deviceInfo, customerName, groupName) {
    if (customerName != null) {
        deviceInfo.customerName = customerName;
    }
    if (groupName != null) {
        deviceInfo.groupName = groupName;
    }
}

function processTelemetryData(telemetry, addDataToTelemetry) {
    if (telemetry.size > 1) {
        telemetry = addDataToMultipleTelemetries(telemetry, addDataToTelemetry);
    }
    else if (telemetry.size == 1) {
        telemetry = addDataToSingleTelemetry(telemetry, addDataToTelemetry);
    }
    else {
        telemetry.add(addDataToTelemetry);
    }

    return telemetry;
}

function addDataToMultipleTelemetries(telemetry, addDataToTelemetry) {
    foreach(element : addDataToTelemetry.entrySet()) {
        if(!telemetry[0]["values"].keys.contains(element.key)) {
            telemetry[0]["values"][element.key] = element.value;
        }
        if (!telemetry[1]["values"].keys.contains(element.key)) {
            telemetry[1]["values"][element.key] = element.value;
        }
    }

    return telemetry;
}

function addDataToSingleTelemetry(telemetry, addDataToTelemetry) {
    foreach(element : addDataToTelemetry.entrySet()) {
            if(!telemetry[0]["values"].keys.contains(element.key)) {
                telemetry[0]["values"][element.key] = element.value;
            }
    }

    return telemetry;
}

function calculatePreviousState(compressedDuration) {
    var state = {};
    state.previous_state_duration_overflow = false;

    if (compressedDuration < 90) {
      state.previous_state_duration = compressedDuration;
      state.previous_state_duration_error = 0;
    } else if (compressedDuration >= 90 && compressedDuration < 120) {
      state.previous_state_duration = 90 + (compressedDuration - 90) * 5;
      state.previous_state_duration_error = 4;
    } else if (compressedDuration >= 120 && compressedDuration < 127) {
      result.previous_state_duration = 240 + (compressedDuration - 120) * 60;
      result.previous_state_duration_error = 59;
    } else if (compressedDuration === 127) {
      state.previous_state_duration = 660;
      state.previous_state_duration_error = null;
      state.previous_state_duration_overflow = true;
    }

    return state;
  }
