// Decode an uplink message from a buffer
// payload - array of bytes
// metadata - key/value object

/** Decoder **/

// decode payload to string
var payloadStr = decodeToString(payload);

// decode payload to JSON
// var data = decodeToJson(payload);

var deviceName = 'Device A';
var deviceType = 'thermostat';
var customerName = 'Customer C';
var groupName = 'thermostat devices';
var manufacturer = 'Example corporation';
// use assetName and assetType instead of deviceName and deviceType
// to automatically create assets instead of devices.
// var assetName = 'Asset A';
// var assetType = 'building';

// Result object with device/asset attributes/telemetry data
var result = {
// Use deviceName and deviceType or assetName and assetType, but not both.
   deviceName: deviceName,
   deviceType: deviceType,
// assetName: assetName,
// assetType: assetType,
// customerName: customerName,
   groupName: groupName,
   attributes: {
       model: 'Model A',
       serialNumber: 'SN111',
       integrationName: metadata['integrationName'],
       manufacturer: manufacturer
   },
   telemetry: {
       temperature: 42,
       humidity: 80,
       rawData: payloadStr
   }
};

/** Helper functions **/

function decodeToString(payload) {
   return String.fromCharCode.apply(String, payload);
}

function decodeToJson(payload) {
   // covert payload to string.
   var str = decodeToString(payload);

   // parse string to JSON
   var data = JSON.parse(str);
   return data;
}

return result;

configuration.tbelDecoder:
var data = decodeToJson(payload);
var deviceName = data.EUI;
var deviceType = "Flux";
var groupName = null; // If groupName is not null - created device will be added to the entity group with such name.
var customerName = null; // If customerName is not null - created devices will be assigned to customer with such name. 

// use assetName and assetType instead of deviceName and deviceType
// to automatically create assets instead of devices.
// var assetName = 'Asset A';
// var assetType = 'building';

// If you want to parse incoming data somehow, you can add your code to this function.
// input: bytes
// expected output:
//  {
//    "attributes": {"attributeKey": "attributeValue"},
//    "telemetry": {"telemetryKey": "telemetryValue"}
//  }

function decodePayload(input) {
    var output = { attributes: {}, telemetry: []};

    var decoded = {};
    var fPort = data.port;
    if(fPort == 10) {
        for(var i = 0; i < input.length - 2; ) {
            var key_1 = input[i++] & 0xff;
            var key_2 = input[i++] & 0xff;

            if(key_1 == 0x00 && key_2 == 0xFE) {
                decoded.energy_consumption_meter_elapsed = parseBytesToInt(input, i, 4);
                decoded.energy_consumption_meter_consumed = parseBytesToInt(input, i + 4, 4);
                i += 8;
            }
            else if(key_1 == 0x00 && key_2 == 0x00) {
                decoded.energy_consumption_meter_status = parseBytesToInt(input, i, 1);
                if(decoded.energy_consumption_meter_status != 0) {
                    decoded.energy_consumption_meter_status != 1;
                }
            }
            else if(key_1 == 0x00 && key_2 == 0x74) {
                decoded.voltmeter = (input[i] << 8) | (input[i + 1] & 0xFF);
                if (decoded.voltmeter != 65535) {
                        decoded.voltmeter = decoded.voltmeter * 10;
                }

                i += 2;
            }
            else if(key_1 == 0x00 && key_2 == 0x75) {
                decoded.voltmeter = (input[i] << 8) | (input[i + 1] & 0xFF);
                if (decoded.ammeter  != 65535) {
                        decoded.ammeter  = decoded.ammeter  * 10;
                }

                i += 2;
            }
            else if(key_1 == 0x00 && key_2 == 0x80) {
                decoded.real_power = (input[i] << 8) | (input[i + 1] & 0xFF);
                if (decoded.real_power  != 65535) {
                        decoded.real_power  = decoded.real_power  * 10;
                }

                i += 2;
            }
            else if(key_1 == 0x01 && key_2 == 0x80) {
                decoded.apparent_power = (input[i] << 8) | (input[i + 1] & 0xFF);
                if (decoded.apparent_power  != 65535) {
                        decoded.apparent_power  = decoded.apparent_power  * 10;
                }

                i += 2;
            }
            else if(key_1 == 0x00 && key_2 == 0x81) {
                decoded.power_factor_meter = input[i] & 0xFF;
                if (decoded.power_factor_meter  != 65535) {
                        decoded.power_factor_meter  = decoded.power_factor_meter  * 100;
                }

                i += 1;
            }
            else if(key_1 == 0x00 && key_2 == 0x01) {
                decoded.relay_status = input[i] & 0xFF;

                if (decoded.relay_status != 0) {
                        decoded.relay_status = 1;
                }

                i += 1;
            }
        }
    }
    else if (fPort == 100) {
        for(var i = 0; i < input.length -1; ) {
            var key = input[i++] & 0xff;

            if(key == 0x00) {
                output.attributes.eui = bytesToHex(java.util.Arrays.copyRange(input, i, i + 8));
                i += 8;
            }
            else if(key == 0x01) {
                output.attributes.app_eui = bytesToHex(java.util.Arrays.copyRange(input, i, i + 8));
				i += 8;
            }
            else if (key == 0x02) {
                output.attributes.app_key = bytesToHex(java.util.Arrays.copyRange(input, i, i + 16));
                i += 16;
            }
            else if (key == 0x03) {
                output.attributes.devAddr = bytesToHex(java.util.Arrays.copyRange(input, i, i + 4));
                i += 4;
            }
            else if(key == 0x04) {
                output.attributes.network_session_key = bytesToHex(java.util.Arrays.copyRange(input, i, i + 16));
                i += 16;
            }
            else if(key == 0x05) {
                output.attributes.app_session_key = bytesToHex(java.util.Arrays.copyRange(input, i, i + 16));
                i += 16;
            }
            else if(key == 0x10) {
                output.attributes.loramac_join_mode = (input[i] >> 7) & 1;
                i += 2;
            }
            else if (key == 0x11) {
                var val = (input[i] >> 4) & 0x0F;

                if(val == 0x0C) {
                    output.attributes.lora_class = "Class C";
                }
                else if(val == 0x0B) {
                    output.attributes.lora_class = "Class B";
                }
                else if(val == 0x0A) {
                    output.attributes.lora_class = "Class F";
                }
                else {
                    output.attributes.lora_class = "Invalid";
                }

				output.attributes.confirm_mode = (input[i] >> 8) & 1; 
				output.attributes.sync_word = (input[i] >> 9) & 1;
				output.attributes.duty_cycle = (input[i] >> 10) & 1;
				output.attributes.adr = (input[i] >> 11) & 1; 

				i += 2;
            }
            else if (key == 0x12) {
                output.attributes.dr_number = (((input[i] << 8) | input[i + 1]) >> 8) & 0xF;
                output.attributes.tx_power_number = ((input[i] << 8) | input[i + 1]) & 0xF;

                i +=2;
            }
            else if (key == 0x13) {
                output.attributes.frequency = (((input[i] << 32) | (input[i + 1] << 24) | (input[i + 2] << 16) | (input[i + 3] << 8) | input[i + 4]) >> 8) & 0xFFFFFFFF;
                output.attributes.dr_number_rx2 = input[i + 4] & 0xFF;

                i += 5;
            }
            else if(key == 0x19) {
                output.attributes.netid_msb = parseBytesToInt(input, i, 2);
                i += 2;
            }
            else if (key == 0x20) {
                output.attributes.seconds_per_core_tick = parseBytesToInt(input, i, 4);
                i += 4;
            }
            else if(key == 0x21) {
                output.attributes.tick_per_battery = parseBytesToInt(input, i, 2);
                i += 2;
            }
            else if(key == 0x71) {
                output.attributes.app_major_version = input[i];
				output.attributes.app_minor_version = input[i + 1];
				output.attributes.app_revision = input[i + 2];
				output.attributes.loramac_major_version = input[i + 3];
				output.attributes.loramac_minor_version = input[i + 4];
				output.attributes.loramac_revision = input[i + 5];
				output.attributes.region = input[i + 7];
				i += 7;
            }
        }
    }

    output.telemetry = [{
        ts: timestamp,
        values: decoded
    }];

    // --- Decoding code --- //
    return output;
}

// --- attributes and telemetry objects ---
var telemetry = [];
var attributes = {};
// --- attributes and telemetry objects ---

// --- Timestamp parsing
timestamp = data.ts;
// --- Timestamp parsing

// Message parsing
// To avoid paths in the decoded objects we passing false value to function as "pathInKey" argument.
// Warning: pathInKey can cause already found fields to be overwritten with the last value found.

var uplinkDataList = [];

// Passing incoming bytes to decodePayload function, to get custom decoding
var customDecoding = decodePayload(hexToBytes(data.data));

// Collecting data to result
if (customDecoding.?telemetry.size() > 0) {
    if (customDecoding.telemetry instanceof java.util.ArrayList) {
        foreach(telemetryObj: customDecoding.telemetry) {
            if (telemetryObj.ts != null && telemetryObj.values != null) {
                telemetry.add(telemetryObj);
            }
        }
    } else {
        telemetry.putAll(customDecoding.telemetry);
    }
}

if (customDecoding.?attributes.size() > 0) {
    attributes.putAll(customDecoding.attributes);
}

// You can add some keys manually to attributes or telemetry
attributes.eui = data.EUI;
attributes.fPort = data.port;
attributes.frequency = data.freq;

var isIncludeGatewayInfo = metadata["includeGatewayInfo"];
if(isIncludeGatewayInfo == true) {
    var addDataToTelemetry = {};
    addDataToTelemetry.rssi = data.rssi;
    addDataToTelemetry.seqno = data.seqno;
    addDataToTelemetry.snr = data.snr;
    addDataToTelemetry.ack = data.ack;
    addDataToTelemetry.toa = data.toa;
    addDataToTelemetry.fCnt = data.fcnt;

   telemetry = processTelemetryData(telemetry, addDataToTelemetry);
}

var deviceInfo = {
    deviceName: deviceName,
    deviceType: deviceType,
//  assetName: assetName,
//  assetType: assetType,
    attributes: attributes,
    telemetry: telemetry, 
};

addAdditionalInfoForDeviceMsg(deviceInfo, customerName, groupName);

uplinkDataList.add(deviceInfo);

var gatewayDeviceNamePrefix = "Gateway ";
var gatewayDeviceType = "Lora gateway";
var gatewayGroupName = null; // If gatewayGroupName is not null - created device will be added to the entity group with such name.

if (data.cmd == "gw") {
    foreach( gatewayInfo : data.gws ) {
        var addGatewayInfo = {};

        // You can add some keys manually telemetry
        addGatewayInfo.rssi = gatewayInfo.rssi;
        addGatewayInfo.snr = gatewayInfo.snr;
        // You can add some keys manually telemetry

        var gatewayInfoMsg = {
            deviceName: gatewayDeviceNamePrefix + gatewayInfo.gweui,
            deviceType: gatewayDeviceType,
            telemetry: [{
                "ts": gatewayInfo.ts,
                "values": addGatewayInfo
            }],
            attributes: {
                eui: gatewayInfo.gweui
            }
        };
        addAdditionalInfoForDeviceMsg(gatewayInfoMsg, customerName, gatewayGroupName);
        uplinkDataList.add(gatewayInfoMsg);
    }
}

return uplinkDataList;

function addAdditionalInfoForDeviceMsg(deviceInfo, customerName, groupName) {
    if (customerName != null) {
        deviceInfo.customerName = customerName;
    }
    if (groupName != null) {
        deviceInfo.groupName = groupName;
    }
}

function processTelemetryData(telemetry, addDataToTelemetry) {
    if (telemetry.size >= 1) {
        telemetry = addDataToTelemetries(telemetry, addDataToTelemetry);
    }
    else {
        telemetry.add(addDataToTelemetry);
    }

    return telemetry;
}

function addDataToTelemetries(telemetries, addDataToTelemetry) {
    foreach(telemetry : telemetries) {
        foreach(element : addDataToTelemetry.entrySet()) {
            if(!telemetry["values"].keys.contains(element.key)) {
                telemetry["values"][element.key] = element.value;
            }
        } 
    }

    return telemetries;
}
